func TestControllerTemplatePost1(t *testing.T) {
	token := testUtil.GetToken()
	testhost, _ := testUtil.GetHost()
	requestURL := fmt.Sprintf("http://%v/cluster/v1/template", testhost)
	_, httpStatus := testUtil.RequestTest(t, requestURL, http.MethodPost, []byte(cluster_template1), map[string]string{"token": token}, true, true)
	testUtil.HttpStatusIsExpected(t, []int{http.StatusOK}, httpStatus)
}

func TestControllerTemplatePost2(t *testing.T) {
	token := testUtil.GetToken()
	testhost, _ := testUtil.GetHost()
	requestURL := fmt.Sprintf("http://%v/cluster/v1/template", testhost)
	_, httpStatus := testUtil.RequestTest(t, requestURL, http.MethodPost, []byte(cluster_template2), map[string]string{"token": token}, true, true)
	testUtil.HttpStatusIsExpected(t, []int{http.StatusBadRequest}, httpStatus)
}

func TestControllerTemplateGet(t *testing.T) {
	token := testUtil.GetToken()
	testhost, _ := testUtil.GetHost()
	requestURL := fmt.Sprintf("http://%v/cluster/v1/template", testhost)
	_, httpStatus := testUtil.RequestTest(t, requestURL, http.MethodGet, nil, map[string]string{"token": token}, true, true)
	testUtil.HttpStatusIsExpected(t, []int{http.StatusOK}, httpStatus)
}
